import styled from "styled-components";
import { divLink as Link } from 'react-router-dom';
import { FaBar } from 'react-icon';

export const div = styled.div`
    background: #000;
    height: 80px;
    display: flex;
    justify-content: space-between;
    padding: 0.5rem calc((100vw - 1000px) / 2);
    z-index: 10;
`

export const divLink = styled(Link)`
    color: #fff;
    display: flex;
    align-item: center;
    text-decoration: none;
    padding: 0 lrem;
    height: 100%;
    cursor: pointer;

    &.active {
        color: #15cdfc;
    }
`

export const Bars = styled(FaBar)`
    display: none;
    color: #fff;

    @media screen and (max-width: 768px) {
        display: block;
        position: absolute;
        top: 0;
        right: 0;
        transfrom: translate(-100, 75%);
        font-size: 1.8rem;
        cursor: pointer;
    }
`
export const divMenu = styled.div`
    display: flex;
    align-item: center;
    margin-right: -24px;

    @media screen and (max-width: 768px) {
        display: none;
    }
`
