import React, { useState, useEffect } from 'react'
import MicIcon from '@material-ui/icons/Mic';
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import { Button } from '@material-ui/core';
import { createTheme } from '@material-ui/core/styles';
import '../App.css'

import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Fab from '@material-ui/core/Fab';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Zoom from '@material-ui/core/Zoom';

import axios from 'axios';
import { DefaultPlayer as Video } from 'react-html5video';
import 'react-html5video/dist/styles.css';
import { red } from '@material-ui/core/colors';

// Speech to text
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()
const useStyles = makeStyles((theme) => ({
  appBar: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}));

// Variable mic
mic.continuous = true
mic.interimResults = true
mic.lang = 'th-Th'

function Content(props) {
  // Variable useState
  const [isListening, setIsListening] = useState(false)
  const [note, setNote] = useState(null)
  const [savedNotes, setSavedNotes] = useState([])
  const [cutWordTH, setCutWordTH] = useState([])

  // Create theme
  const theme = createTheme({
    palette: {
      red: {
        main: '#d50000',
      }
    }
  })

  // Scoll to top
  const { children, window } = props;
  const classes = useStyles();
  const trigger = useScrollTrigger({
    target: window ? window() : undefined,
    disableHysteresis: true,
    threshold: 100,
  });

  // Click scoll to top button
  const handleClick = (event) => {
    const anchor = (event.target.ownerDocument || document).querySelector('#back-to-top-anchor');

    if (anchor) {
      anchor.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  };

  var wordList = {};

  // Variable useEffect
  useEffect(() => {
    handleListen();
  }, [isListening])

  // Click start/stop button
  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }

    mic.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      console.log(transcript)
      setNote(transcript)

      let config = { 'Apikey': 'rBOeYWQF1L4QEfhffQB7ubqYAJqYSWvQ' };
      //return axios.get(`${apiBaseURL}/v1/cryptocurrency/map`, { headers: config })

      axios.get("https://api.aiforthai.in.th/tlexplus?text=" + transcript, { headers: config }).then((response) => {
        wordList = response.data.tokens;
        console.log(wordList);
       
        setCutWordTH(wordList);

      }
      ).catch((error) => {
        // alert(error)
      });

      mic.onerror = event => {
        console.log(event.error)
      }
    }
  }

  // const handleSaveNote = () => {
  //   setSavedNotes([...savedNotes, note])
  //   setNote('')
  // }

  return (
    <Zoom in={trigger}>
      <div onClick={handleClick} role="presentation" className={classes.appBar}>
        {children}
      </div>
    </Zoom>
  )
}

Content.propTypes = {
  children: PropTypes.element.isRequired,
  window: PropTypes.func,
};

export default function BackToTopContent(props) {
  return (
    <React.Fragment>
      <CssBaseline />
      <Toolbar id="back-to-top-anchor" />
      
        <Content {...props}>
          <div>
            <center>
              <h1>โปรแกรมศึกษาภาษามือ</h1>
            </center>
          </div>
        </Content>
        <ScrollTop {...props}>
          <Fab color="secondary" size="small" aria-label="scroll back to top">
            <KeyboardArrowUpIcon />
          </Fab>
        </ScrollTop>
    </React.Fragment>
  )
}
