import React, { useState, useEffect } from 'react'
import MicIcon from '@material-ui/icons/Mic';
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import { Button } from '@material-ui/core';
import { createTheme } from '@material-ui/core/styles';
import '../App.css'

import PropTypes from 'prop-types';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';
import Fab from '@material-ui/core/Fab';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Zoom from '@material-ui/core/Zoom';

import axios from 'axios';
import { DefaultPlayer as Video } from 'react-html5video';
import 'react-html5video/dist/styles.css';
import { red } from '@material-ui/core/colors';
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'th-Th'

const useStyles = makeStyles((theme) => ({
  root: {
    position: 'fixed',
    bottom: theme.spacing(2),
    right: theme.spacing(2),
  },
}));

function App(props) {
  const [isListening, setIsListening] = useState(false)
  const [note, setNote] = useState(null)
  const [savedNotes, setSavedNotes] = useState([])
  const [cutWordTH, setCutWordTH] = useState([])
  const theme = createTheme({
    palette: {
      red: {
        main: '##d50000',
      }
    }
  })
  const { children, window } = props;
  const classes = useStyles();
  const trigger = useScrollTrigger({
    target: window ? window() : undefined,
    disableHysteresis: true,
    threshold: 100,
  });
  const handleClick = (event) => {
    const anchor = (event.target.ownerDocument || document).querySelector('#back-to-top-anchor');

    if (anchor) {
      anchor.scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  };
  

  var wordList = {};

  useEffect(() => {
    handleListen();
  }, [isListening])

  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }

    mic.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      console.log(transcript)
      setNote(transcript)

      let config = { 'Apikey': 'rBOeYWQF1L4QEfhffQB7ubqYAJqYSWvQ' };
      //return axios.get(`${apiBaseURL}/v1/cryptocurrency/map`, { headers: config })

      axios.get("https://api.aiforthai.in.th/tlexplus?text=" + transcript, { headers: config }).then((response) => {
        wordList = response.data.tokens;
        console.log(wordList);
       
        setCutWordTH(wordList);

      }
      ).catch((error) => {
        // alert(error)
      });

      mic.onerror = event => {
        console.log(event.error)
      }
    }
  }

  // const handleSaveNote = () => {
  //   setSavedNotes([...savedNotes, note])
  //   setNote('')
  // }

  return (
    <>
      <Zoom in={trigger}>
        <div onClick={handleClick} role="presentation" className={classes.root}>
          {children}
        </div>
      </Zoom>
      <div style={{ marginTop: 100 }}>
        <center>
          <h1>โปรแกรมศึกษาภาษามือ</h1>
          <h2>คำที่คุณพูด</h2>
          <div className='Box'>
            {note}
          </div>
        </center>
        <br />

        <center>
          {/* 🎙️ 🛑*/}
          {isListening 
            ? <span>
                <MicIcon color='action' />
              </span> 
            : <span>
                <FiberManualRecordIcon style={{ color: red[500] }} />
                <MicIcon color='action' />
              </span>}
          <br />

          <Button variant='contained' color='primary' 
            onClick={() => setIsListening(prevState => !prevState)}>
              Start/Stop
          </Button>
          {/* <button onClick={handleSaveNote} disabled={!note}>
            Save Note
          </button> */}
        </center>
        {/* <center>
          <h2>Translate</h2>
          <div className='BoxTranslate'>
            <p>{cutWordTH}</p>
          </div>
        </center> */}
        <br />

        <center>
          <table className='tab' border="1">
            <tr>
              <th width='80px; text-align=center;'>คำ</th>
              <th width='80px; text-align=center;'>วีดีโอ</th>
            </tr>
            {cutWordTH.map(word =>{
              return <tr key={word}>
                <td id='word'>
                  {word}
                </td>
                <td>
                  <br />

                    <Video className='Video' mute
                      controls={['PlayPause', 'Seek', 'Time', 'Fullscreen']}
                      onCanPlayThrough={() => {
                          // Do stuff
                      }}>
                      <source src={`video/${word}.mov`} type="video/webm" />
                    </Video>
                  <br />

                </td>
              </tr>
            })}
          </table>
        </center>
      </div >
    </>
  )
}

ScrollTop.propTypes = {
  children: PropTypes.element.isRequired,
  /**
   * Injected by the documentation to work in an iframe.
   * You won't need it on your project.
   */
  window: PropTypes.func,
};

export default App
