import React, { useState, useEffect } from 'react'
import './App.css'

import axios from 'axios';
import { DefaultPlayer as Video } from 'react-html5video';
import 'react-html5video/dist/styles.css';
const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'th-Th'

// Word colide video 

// const videoDB = [
//   : "‡∏™‡∏ö‡∏≤‡∏¢.MOV"},
//   : "‡∏î‡∏µ.MOV"},
//   : "‡πÑ‡∏´‡∏°.MOV"}
// ]

// videoUse = []
// for(let i = 0; i < wordlist.length; i++) {
//     videoUse.append( videoDB[ wordlist[i] ] )
// }


function App() {
  const [isListening, setIsListening] = useState(false)
  const [note, setNote] = useState(null)
  const [savedNotes, setSavedNotes] = useState([])
  const [cutWordTH, setCutWordTH] = useState([])

  var wordList = {};

  useEffect(() => {
    handleListen();
  }, [isListening])

  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }

    mic.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      console.log(transcript)
      setNote(transcript)

      let config = { 'Apikey': 'rBOeYWQF1L4QEfhffQB7ubqYAJqYSWvQ' };
      //return axios.get(`${apiBaseURL}/v1/cryptocurrency/map`, { headers: config })

      axios.get("https://api.aiforthai.in.th/tlexplus?text=" + transcript, { headers: config }).then((response) => {
        wordList = response.data.tokens;
        console.log(wordList);
       
        setCutWordTH(wordList);

      }
      ).catch((error) => {
        // alert(error)
      });

      mic.onerror = event => {
        console.log(event.error)
      }
    }
  }

  const handleSaveNote = () => {
    setSavedNotes([...savedNotes, note])
    setNote('')
  }

  return (
    <>
      <div style={{ marginTop: 100 }}>
        <center>
          <h1>‡πÇ‡∏õ‡∏£‡πÅ‡∏Å‡∏£‡∏°‡∏ä‡πà‡∏ß‡∏¢‡∏®‡∏∂‡∏Å‡∏©‡∏≤‡∏†‡∏≤‡∏©‡∏≤‡∏°‡∏∑‡∏≠</h1>
          <h2>‡∏Ñ‡∏≥‡∏ó‡∏µ‡πà‡∏Ñ‡∏∏‡∏ì‡∏û‡∏π‡∏î</h2>
          <div className='Box'>
            {note}
          </div>
        </center>
        <br />

        <center>
          {isListening ? <span>üéôÔ∏è</span> : <span>üõëüéôÔ∏è</span>}
          <br />
          <button onClick={() => setIsListening(prevState => !prevState)}>
            Start/Stop
          </button>
          {/* <button onClick={handleSaveNote} disabled={!note}>
            Save Note
          </button> */}
        </center>
        {/* <center>
          <h2>Translate</h2>
          <div className='BoxTranslate'>
            <p>{cutWordTH}</p>
          </div>
        </center> */}

        <br />
        <center>
          <table className='tab' border="1">
            <tr>
              <th width='80px, text-align='center>‡∏Ñ‡∏≥</th>
              <th>‡∏ß‡∏µ‡∏î‡∏µ‡πÇ‡∏≠</th>
            </tr>
            {cutWordTH.map(word =>{
              return <tr key={word}>
                <td>
                  {word}
                </td>
                <td>
                    <Video className='Video'
                      controls={['PlayPause', 'Seek', 'Time', 'Volume', 'Fullscreen']}
                      onCanPlayThrough={() => {
                          // Do stuff
                      }}>
                      <source src={`video/${word}.mov`} type="video/webm" />
                    </Video>
                </td>
              </tr>
            })}
          </table>
        </center>
      </div>
    </>
  )
}

export default App
