import React, { useState, useEffect } from 'react'
import { Box, Paper, Typography } from '@material-ui/core'
import './App.css'

import axios from 'axios';

const SpeechRecognition =
  window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'th-Th'

//https://www.npmjs.com/package/thai-wordcut

//https://www.npmjs.com/package/tnthai

/*  Example  

const videoDB = {
  "‡∏™‡∏ß‡∏±‡∏™‡∏î‡∏µ" : "s1.mp4",
  "‡πÑ‡∏õ‡πÑ‡∏´‡∏ô‡∏°‡∏≤" : "s2.mp4",
}


videoUse = []
for (i = 0;i < wordlist.length;i++){
    videoUse.append( videoDB[ wordlist[i] ] )
}


*/


function App() {
  const [isListening, setIsListening] = useState(false)
  const [note, setNote] = useState(null)
  const [savedNotes, setSavedNotes] = useState([])

  var wordList ={};
  
  useEffect(() => {
    //wordCut.init();
    handleListen();
  }, [isListening])

  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }

    mic.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      console.log(transcript)
      setNote(transcript)

      /*let config = { 'Apikey': 'rBOeYWQF1L4QEfhffQB7ubqYAJqYSWvQ' };
      //return axios.get(`${apiBaseURL}/v1/cryptocurrency/map`, { headers: config })
    
      axios.get("https://api.aiforthai.in.th/tlexplus?text="+transcript, { headers : config } ).then( (response)=>{
             wordList = response.data.tokens;
             alert(wordList);
         }
      ).catch( (error) => {
        alert(error)
      });*/

      mic.onerror = event => {
        console.log(event.error)
      }
    }
  }

  const handleSaveNote = () => {
    setSavedNotes([...savedNotes, note ])
    setNote('')
  }

  return (
    <>
      <div style={{marginTop:100}}>
            <center>
            <h1>Voice Notes</h1>
            <h2>Current Note</h2>
            <Box sx={{ width:50 }} >
              <Paper elevation="24"  >
                  <Typography> {note} </Typography>
              </Paper>
            </Box>

            </center>

            <center>
                {isListening ? <span>üéôÔ∏è</span> : <span>üõëüéôÔ∏è</span>}
                <br />
                <button onClick={() => setIsListening(prevState => !prevState)}>
                    Start/Stop
                </button>
                <button onClick={handleSaveNote} disabled={!note}>
                    Save Note
                </button>
            </center>

        <center>
          <h2>Translate</h2>
          <textarea id="input_area" name="message" rows="10" cols="100" ></textarea>
          {savedNotes.map(n => (
            <p key={n}>{n}</p>
          ))}
        </center>
      </div>
    </>
  )
}

export default App
