import { Button, Container, Typography, Box } from "@mui/material";
import MicIcon from '@material-ui/icons/Mic';
import React, { useState, useEffect, Fragment } from "react";
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';

import axios from 'axios';
import { DefaultPlayer as Video } from 'react-html5video';
import 'react-html5video/dist/styles.css';
import { red } from '@material-ui/core/colors';

const SpeechRecognition =
    window.SpeechRecognition || window.webkitSpeechRecognition
const mic = new SpeechRecognition()

mic.continuous = true
mic.interimResults = true
mic.lang = 'th-Th'

function VoiceToHand() {
    const [isListening, setIsListening] = useState(false)
    const [note, setNote] = useState(null)
    const [savedNotes, setSavedNotes] = useState([])
    const [cutWordTH, setCutWordTH] = useState([])

  var wordList = {};

  useEffect(() => {
    handleListen();
  }, [isListening])

  const handleListen = () => {
    if (isListening) {
      mic.start()
      mic.onend = () => {
        console.log('continue..')
        mic.start()
      }
    } else {
      mic.stop()
      mic.onend = () => {
        console.log('Stopped Mic on Click')
      }
    }
    mic.onstart = () => {
      console.log('Mics on')
    }

    mic.onresult = event => {
      const transcript = Array.from(event.results)
        .map(result => result[0])
        .map(result => result.transcript)
        .join('')
      console.log(transcript)
      setNote(transcript)

      let config = { 'Apikey': 'rBOeYWQF1L4QEfhffQB7ubqYAJqYSWvQ' };
      //return axios.get(`${apiBaseURL}/v1/cryptocurrency/map`, { headers: config })

      axios.get("https://api.aiforthai.in.th/tlexplus?text=" + transcript, { headers: config }).then((response) => {
        wordList = response.data.tokens;
        console.log(wordList);
       
        setCutWordTH(wordList);

      }
      ).catch((error) => {
        // alert(error)
      });

      mic.onerror = event => {
        console.log(event.error)
      }
    }
  }


    return (
        <Box 
         mt={10}
         textAlign='center'
        >
            <Typography variant="h4" textAlign="center"> โปรแกรมศึกษาภาษามือ </Typography>
            <br />
            <Typography variant="h6" textAlign="center"> Text </Typography>
            <br />

            <Container
             textAlign='center'
             sx={{
                width: 500,
                height: 100,
                border: '1px solid black',
             }}
            >
                {note}
            </Container>
            <br />

            <Container textAlign='center'>
                {isListening ? <span> <MicIcon color='action'/> </span> : 
                               <span> 
                                   <FiberManualRecordIcon style={{ color: red[500] }} /> 
                                   <MicIcon color="action" /> 
                                </span>}
                <br />
                
                <Button variant='contained' color='primary'
                 onClick={() => setIsListening(prevState => !prevState)}
                >
                    Start/Stop
                </Button>
            </Container>
            <br />

            <Container textAlign='center'>
                <table border='1'>
                    <tr>
                        <th width='80px; text-align=center;'>คำ</th>
                        <th width='80px; text-align=center;'>วีดีโอ</th>
                    </tr>
                    {cutWordTH.map(word =>{
                        return <tr key={word}>
                        <td id='word'>
                        {word}
                        </td>
                        <td>
                            <Video className='Video' muted
                            controls={['PlayPause', 'Seek', 'Time', 'Fullscreen']}
                            onCanPlayThrough={() => {
                                // Do stuff
                            }}>
                            <source src={`video/${word}.mov`} type="video/webm" />
                            </Video>
                        </td>
                    </tr>
                    })}
                </table>
            </Container>
        </Box>
    );
}

export default VoiceToHand;
